import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ThankYou = () => {
  const [fullname, setFullname] = useState("");
  const [phone, setPhone] = useState("");
  const navigate = useNavigate();

  const handleCancelBooking = async () => {
    try {
      // Send a request to the backend to cancel the booking based on fullname or phone number
      await axios.delete("http://localhost:5000/bookings/cancel-booking", {
        data: { fullname, phone },
      });
      alert("Booking canceled successfully.");
      navigate("/");
    } catch (error) {
      alert("Failed to cancel booking: " + error.response?.data?.message);
    }
  };

  return (
    <div>
      <h2>Thank you for your booking!</h2>
      <p>If you need to cancel your booking, please provide your details below:</p>
      <input
        type="text"
        placeholder="Enter your fullname"
        value={fullname}
        onChange={(e) => setFullname(e.target.value)}
      />
      <input
        type="text"
        placeholder="Enter your phone number"
        value={phone}
        onChange={(e) => setPhone(e.target.value)}
      />
      <button onClick={handleCancelBooking}>Cancel Booking</button>
    </div>
  );
};

export default ThankYou;
