// import React, { useState } from "react";
import './booking.css';
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";

const Booking = ({ tour, avgRating }) => {
    const { price, reviews } = tour;
    const [credentials, setCredentials] = useState({
        userId: '01',
        userEmail: 'manmeet@gmail.com',
        fullName: '',
        phone: '',
        guestSize: 1,
        bookAt: '',
    });
    const Navigate = useNavigate();
    const ServiceFee = 10;
    const totalAmount = Number(price) * Number(credentials.guestSize) + Number(ServiceFee);

    const handleChange = e => {
        setCredentials(prev => ({ ...prev, [e.target.id]: e.target.value }));
    };

    // Send data to server
    const handleClick = async e => {
        e.preventDefault();

        if (!credentials.fullName || !credentials.phone || !credentials.bookAt || !credentials.guestSize) {
            alert('Please fill in all the required fields.');
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/api/bookings', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ ...credentials, tourId: tour.id })
            });

            const result = await response.json();

            if (response.ok) {
                alert('Booking successful!');
                Navigate("/thank-you");
            } else {
                alert(`Booking failed: ${result.message}`);
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Booking failed: An error occurred');
        }
    };

    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>${price}<span>/per person</span></h3>
                <span className="tour__rating d-flex align-items-center">
                    <i className="ri-star-fill"></i>
                    {avgRating === 0 ? null : avgRating}({reviews?.length})
                </span>
            </div>
            {/* Booking form */}
            <div className="booking__form">
                <h5>Information</h5>
                <Form className="booking__info-form" onSubmit={handleClick}>
                    <FormGroup>
                        <input type="text" placeholder="Full Name" id="fullName" required onChange={handleChange} />
                    </FormGroup>
                    <FormGroup>
                        <input type="number" placeholder="Phone" id="phone" required onChange={handleChange} />
                    </FormGroup>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input type="date" placeholder="" id="bookAt" required onChange={handleChange} />
                        <input type="number" placeholder="Guest" id="guestSize" required onChange={handleChange} />
                    </FormGroup>
                    <Button className="btn primary__btn w-100 mt-4" type="submit">Book Now</Button>
                </Form>
            </div>
            {/* Booking bottom */}
            <div className="booking__bottom">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">${price} <i className="ri-close-line">1 person</i></h5>
                        <span>${price}</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Service Charge</h5>
                        <span>${ServiceFee}</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Total </h5>
                        <span>${totalAmount}</span>
                    </ListGroupItem>
                </ListGroup>
            </div>
        </div>
    );
};

export default Booking;
