import React, { useState } from "react";
import '../styles/login.css';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Form, FormGroup, Button } from "reactstrap";
import LognImg from '../assets/images/login.png';
import userIcon from '../assets/images/user.png';

const Login = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const navigate = useNavigate();

  const handleChange = e => {
    setCredentials(prev => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleClick = async e => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(credentials)
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem('userId', data.user.id); // Store user ID
        localStorage.setItem('userEmail', data.user.email); // Store user email
        alert(data.message);
        navigate('/home');
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg='8' className="m-auto">
            <div className="login__container d-flex justify-content-between">
              <div className="login__img">

              <img src={LognImg} alt="Login" />
              </div>
              <div className="login__form">
                <div className="form__top">
                  <h2>Login</h2>
                </div>
                <Form className="form" onSubmit={handleClick}>
                  <FormGroup className="d-flex align-items-center gap-2">
                    <input
                      type="email"
                      id="email"
                      placeholder="Email"
                      required
                      onChange={handleChange}
                    />
                    <img src={userIcon} alt="User" />
                  </FormGroup>
                  <FormGroup className="d-flex align-items-center gap-2">
                    <input
                      type="password"
                      id="password"
                      placeholder="Password"
                      required
                      onChange={handleChange}
                    />
                  </FormGroup>
                  <Button className="btn primary__btn w-100" type="submit">
                    Login
                  </Button>
                  <p className="mt-3">
                    Don't have an account? <Link to="/register">Register</Link>
                  </p>
                </Form>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Login;

