import React, { useRef, useState } from "react";
import '../styles/tour-details.css';
import { Container, Row, Col, Form,FormGroup, ListGroup, Button, Modal, ModalHeader, ModalBody, ModalFooter, Input } from "reactstrap";
import { useParams, useNavigate } from "react-router-dom";
import tourData from '../assets/data/tours';
import calculateAvgRating from "../utils/avgRating";
import avatar from '../assets/images/avatar.jpg';
import Booking from "../components/Booking/Booking";
import Newsletter from "../shared/Newsletter";


const TourDetails = () => {
    const { id } = useParams();
    const reviewmsgRef = useRef('');
    const [tourRating, setTourRating] = useState(null);
    const [showLoginModal, setShowLoginModal] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [credentials, setCredentials] = useState({ email: '', password: '' });
    const navigate = useNavigate();

    const tour = tourData.find(tour => tour.id === id);
    const { photo, title, desc, price, address, reviews, city, distance, maxGroupSize } = tour;
    const { totalRating, avgRating } = calculateAvgRating(reviews);
    const options = { day: "numeric", month: "long", year: "numeric" };

    const submitHandler = e => {
        e.preventDefault();
        const reviewtext = reviewmsgRef.current.value;
        alert(`${reviewtext} ${tourRating}`);
    };

    const handleBooking = async () => {
        if (!isLoggedIn) {
            setShowLoginModal(true);
            return;
        }

        // Handle the booking process
        const bookingDetails = {
            userId: '01',
            userEmail: 'manmeet@gmail.com',
            fullName: credentials.fullName,
            phone: credentials.phone,
            guestSize: credentials.guestSize,
            bookAt: credentials.bookAt,
            tourId: id,
        };

        try {
            const response = await fetch('http://localhost:5000/api/bookings', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(bookingDetails),
            });
            if (response.ok) {
                navigate('/thank-you');
            } else {
                alert('Booking failed.');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/api/auth/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(credentials),
            });
            const data = await response.json();
            if (response.ok) {
                alert(data.message);
                setIsLoggedIn(true);
                setShowLoginModal(false);
                handleBooking();
            } else {
                alert(data.message);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <>
            <section>
                <Container>
                    <Row>
                        <Col lg="8">
                            <div className="tour_content">
                                <img src={photo} alt="" />
                                <div className="tour_info">
                                    <h2>{title}</h2>
                                    <div className="d-flex align-items-center gap-5">
                                        <span className="tour__rating d-flex align-items-center gap-1">
                                            <i className="ri-star-fill" style={{ 'color': "var(--secondary-color)" }}></i>
                                            {avgRating === 0 ? null : avgRating}
                                            {totalRating === 0 ? ('Not rated') : (<span>({reviews?.length})</span>)}
                                        </span>
                                        <span>
                                            <i className="ri-map-pin-user-fill"></i>{address}
                                        </span>
                                    </div>
                                    <div className="tour__extra-details">
                                        <span>
                                            <i className="ri-map-pin-2-line"></i>{city}
                                            <i className="ri-money-dollar-circle-fill"></i>$ {price} /per person
                                            <i className="ri-map-pin-time-line"></i> {distance} KM
                                            <i className="ri-group-line"></i>{maxGroupSize} People
                                        </span>
                                    </div>
                                    <h5>Description</h5>
                                    <p>{desc}</p>
                                </div>
                                <div className="tour_reviews mt-4">
                                    <h4>Reviews ({reviews?.length} reviews)</h4>
                                    <Form onSubmit={submitHandler}>
                                        <div className="d-flex align-items-center gap-3 mb-4 rating_group">
                                            <span onClick={() => setTourRating(1)}>1<i className="ri-star-s-fill"></i></span>
                                            <span onClick={() => setTourRating(2)}>2<i className="ri-star-s-fill"></i></span>
                                            <span onClick={() => setTourRating(3)}>3<i className="ri-star-s-fill"></i></span>
                                            <span onClick={() => setTourRating(4)}>4<i className="ri-star-s-fill"></i></span>
                                            <span onClick={() => setTourRating(5)}>5<i className="ri-star-s-fill"></i></span>
                                        </div>
                                        <div className="review__input">
                                            <input type="text" ref={reviewmsgRef} placeholder="Share your thoughts" required />
                                            <button className="button btn primary__btn text-white" type="submit">Submit</button>
                                        </div>
                                    </Form>
                                    <ListGroup className="user__reviews">
                                        {reviews?.map(review => (
                                            <div className="review__item" key={review.id}>
                                                <img src={avatar} alt="" />
                                                <div className="w-100">
                                                    <div className="d-flex align-items-center justify-content-between">
                                                        <div>
                                                            <h5>Manmeet Kaur</h5>
                                                            <p>{new Date('09-03-2024').toLocaleDateString("en-US", options)}</p>
                                                        </div>
                                                        <span className="d-flex align-items-center">
                                                            5<i className="ri-star-fill"></i>
                                                        </span>
                                                    </div>
                                                    <h6>Amazing Tour</h6>
                                                </div>
                                            </div>
                                        ))}
                                    </ListGroup>
                                </div>
                            </div>
                        </Col>
                        <Col lg='4'>
                            <Button className="btn primary__btn w-100 mt-4" onClick={handleBooking}>Book Now</Button>
                        </Col>
                    </Row>
                </Container>
            </section>

            {/* Login Modal */}
            <Modal isOpen={showLoginModal} toggle={() => setShowLoginModal(!showLoginModal)}>
                <ModalHeader toggle={() => setShowLoginModal(!showLoginModal)}>Login</ModalHeader>
                <ModalBody>
                    <Form onSubmit={handleLogin}>
                        <FormGroup>
                            <Input type="email" placeholder="Email" id="email" onChange={e => setCredentials({ ...credentials, email: e.target.value })} required />
                        </FormGroup>
                        <FormGroup>
                            <Input type="password" placeholder="Password" id="password" onChange={e => setCredentials({ ...credentials, password: e.target.value })} required />
                        </FormGroup>
                        <Button type="submit" color="primary">Login</Button>
                    </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="secondary" onClick={() => setShowLoginModal(false)}>Cancel</Button>
                </ModalFooter>
            </Modal>

            <Newsletter />
        </>
    );
};

export default TourDetails;

