// TodoList.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '.sty/TodoList.css'; // Optional CSS styling

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState('');
  const [dueDate, setDueDate] = useState('');

  // Fetch all todos from the backend
  useEffect(() => {
    axios.get('/api/todos').then((response) => {
      setTodos(response.data);
    });
  }, []);

  // Add a new todo
  const handleAddTodo = () => {
    if (task.trim()) {
      axios.post('/api/todos/add', { task, dueDate }).then((response) => {
        setTodos([...todos, response.data]);
        setTask('');
        setDueDate('');
      });
    }
  };

  // Toggle the status of a todo
  const handleToggleStatus = (id) => {
    axios.patch(`/api/todos/toggle/${id}`).then((response) => {
      setTodos(
        todos.map((todo) =>
          todo.id === response.data.id ? response.data : todo
        )
      );
    });
  };

  // Delete a todo
  const handleDeleteTodo = (id) => {
    axios.delete(`/api/todos/delete/${id}`).then(() => {
      setTodos(todos.filter((todo) => todo.id !== id));
    });
  };

  return (
    <div className="todo-list">
      <h2>To-Do List</h2>
      <input
        type="text"
        value={task}
        onChange={(e) => setTask(e.target.value)}
        placeholder="Enter a task"
      />
      <input
        type="date"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
      />
      <button onClick={handleAddTodo}>Add Task</button>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            <span>{todo.task}</span> - <span>{todo.dueDate}</span>
            <button onClick={() => handleToggleStatus(todo.id)}>
              {todo.completed ? 'Completed' : 'Pending'}
            </button>
            <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
