import React, { useState } from "react";
import "../styles/tours.css";
import { Container, Row, Col } from "reactstrap";
import TourCard from "../shared/Tourcard";
import SearchBar from "../shared/SearchBar";
import Newsletter from "../shared/Newsletter";
import tourData from "../assets/data/tours";

const Tours = () => {
  const [filteredTours, setFilteredTours] = useState(tourData);

  const handleSearch = (criteria) => {
    const { location, distance, maxGroupSize, price } = criteria;

    const results = tourData.filter((tour) => {
      // Match location if provided
      const matchesLocation = location
        ? tour.city.toLowerCase().includes(location.toLowerCase())
        : true;

      // Match distance if provided
      const matchesDistance = distance
        ? tour.distance <= parseFloat(distance)
        : true;

      // Match max group size if provided
      const matchesGroupSize = maxGroupSize
        ? tour.maxGroupSize >= parseInt(maxGroupSize, 10)
        : true;

      // Match price if provided
      const matchesPrice = price ? tour.price <= parseFloat(price) : true;

      return (
        matchesLocation &&
        matchesDistance &&
        matchesGroupSize &&
        matchesPrice
      );
    });

    setFilteredTours(results);
  };

  return (
    <>
      <section>
        <Container>
          <Row>
            <SearchBar onSearch={handleSearch} />
          </Row>
        </Container>
      </section>
      <section className="pt-5">
        <Container>
          <Row>
            {filteredTours.length > 0 ? (
              filteredTours.map((tour) => (
                <Col lg="3" key={tour.id} className="mb-4">
                  <TourCard tour={tour} />
                </Col>
              ))
            ) : (
              <Col lg="12">
                <h4 className="text-center">No tours found</h4>
              </Col>
            )}
          </Row>
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default Tours;
